    function showModalhandler() {
        setModalIsVisible(true)
    }
    return (
        <>
            <MainHeader showModal={showModalhandler}/> {
            modalIsVisible && (
                <Modal onClose={hideModalHandler}>
                    <NewPost onAuthorChange={authorChangeHandler}
                        onBookChange={bookChangeHandler}/>
