import classes from "./InputWithLabel.module.css";
import { useState } from "react";

const InputWithLabel = ({ label, placeholder, onChange, onBlur, value, error }) => {
  const [isFocussed, setIsFocussed] = useState(false);
  const onFocusHandler = () => {
    setIsFocussed(true);
  }
  console.log(label, isFocussed);
  return (
    <div>
      <label style={{ fontSize: isFocussed ? "1.2rem" : "1.8rem" }} className={classes.label}>{label}</label>
      <br />
      <input
        className={classes.input}
        type="text"
        placeholder={placeholder}
        onBlur={onBlur}
        value={value}
        onChange={onChange}
      ></input>
      <div className={classes.bottomLine}></div>
      <h4>{error}</h4>
    </div>
  );
};

export default InputWithLabel;
