import React, { useRef, useEffect } from "react";
import classes from "./App.module.css";

function App() {
  const num = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const gridElement = document.querySelector(`.${classes.myGrid}`);
    const gridRect = gridElement.getBoundingClientRect();

    // Calculate corners of the grid based on its position on the page
    const topLeft = { x: gridRect.left, y: gridRect.top };
    const bottomRight = { x: gridRect.right, y: gridRect.bottom };
    console.log("topleft", "bottomright", topLeft, bottomRight);
    // Draw diagonal line
    ctx.beginPath();
    ctx.moveTo(topLeft.x, topLeft.y);
    ctx.lineTo(bottomRight.x, bottomRight.y);
    ctx.strokeStyle = "red"; // Set the line color
    ctx.stroke();
  }, []);

  return (
    <div className={classes.myGridContainer}>
      <div className={classes.myGrid}>
        {num.map((num, index) => (
          <h1 key={index} className={classes.myGridItem}>
            {num}
          </h1>
        ))}
      </div>
      <canvas ref={canvasRef}></canvas>
    </div>
  );
}

export default App;
