import classes from "./TreeItem.module.css";
import { useState } from "react";
import TreeList from "./TreeList";
import { FaPlus, FaMinus } from "react-icons/fa";

const TreeItem = ({ item }) => {
  const [clicked, setClicked] = useState(false);
  const [symbol, setSymbol] = useState("<FaPlus/>");

  const onExpandHandler = () => {
    console.log("clicked!");
    setClicked(!clicked);
    if (symbol === "+") {
      return <FaPlus />;
    } else {
      return <FaMinus />;
    }
  };

  return (
    <div>
      <div className={classes.mainBranch}>
        <h3>{item.label}</h3>
        <div onClick={onExpandHandler}>{item.children ? "+" : null}</div>
      </div>
      <div>{clicked && item.children && <TreeList list={item.children} />}</div>
    </div>
  );
};

export default TreeItem;
