import classes from "./Modal.module.css";

const Modal = ({ id, header, body, footer, onClose }) => {
  return (
    <div className={classes.container}>
      <div id={id || "Modal"} className={classes.modal}>
        {/* <div className={classes.content}> */}
        <span onClick={onClose} className={classes.closeModal} size={40}>
          &times;
        </span>
        <div className={classes.header}>
          <h2>{header ? header : "Default header"}</h2>
        </div>
        <div className={classes.body}>{body ? body : "Default Modal Body"}</div>
        <div className={classes.footer}>
          <h2>{footer ? footer : "Default footer"}</h2>
        </div>
        {/* </div> */}
      </div>
    </div>
  );
};

export default Modal;
