import { useState } from "react";
import classes from "./TicTacToe.module.css";

const TicTacToe = () => {
  const [isPlayerX, setPlayerX] = useState(true);
  const [squareArrayValues, setSquareArrayValue] = useState(Array(9).fill(""));

  const handleGameMove = (val) => {
    let array = [...squareArrayValues];
    isPlayerX ? (array[val] = "X") : (array[val] = "O");
  };

  const Square = ({ onClick }) => {
    return <button onClick={onClick} className={classes.square}></button>;
  };

  return (
    <div className={classes.tictactoeContainer}>
      <div>
        <div style={{ display: "flex" }}>
          <Square value={squareArrayValues[1]} onClick={handleGameMove(1)} />
          <Square />
          <Square />
        </div>
        <div style={{ display: "flex" }}>
          <Square />
          <Square />
          <Square />
        </div>
        <div style={{ display: "flex" }}>
          <Square />
          <Square />
          <Square />
        </div>
      </div>
    </div>
  );
};

export default TicTacToe;
