import Post from "./Post"
import classes from "./PostList.module.css"
import NewPost from "./NewPost"
import {useState} from "react"
import Modal from "./Modal"

function PostList() {
    const [enteredBook, setEnteredBook] = useState();
    const [enteredAuthor, setEnteredAuthor] = useState();
    const [modalIsVisible, setModalIsVisible] = useState(true);
    function bookChangeHandler(event) {
        setEnteredBook(event.target.value);
    }
    function authorChangeHandler(event) {
        setEnteredAuthor(event.target.value);
    }
    function hideModalHandler() {
        setModalIsVisible(false);
    }

    return (
        <> {modalIsVisible}?(
            <Modal onClose={hideModalHandler}>
                <NewPost onAuthorChange={authorChangeHandler}
                    onBookChange={bookChangeHandler}/>
            </Modal>
            ):null
            <ul className={
                classes.posts
            }>
                <Post author="Ambili" book="A golden age"/>
                <Post author="Cheran" book="A lovely old secret"/>
                <Post author="Ambili Cheran" book="A grandeaur of a garden"/>
                <Post author={enteredAuthor}
                    book={enteredBook}/>
            </ul>
        </>
    )
}
export default PostList;
