import NavigationBar from "./Components/Navigation/NavigationBar";
import Main from "./Components/Main/Main";
import "./Components/globalStyles.css";
import { useState, useRef } from "react";

const App = () => {
  // const [activeSection, setActiveSection] = useState();
  const portfolioRef = useRef();
  const aboutRef = useRef();
  const contactRef = useRef();

  const onClickPortFolioHandler = () => {
    // setActiveSection("portfolioRef");
    portfolioRef.current.scrollIntoView({ behavior: "smooth" });
  };
  const onClickAboutHandler = () => {
    // setActiveSection("aboutRef");
    aboutRef.current.scrollIntoView({ behavior: "smooth" });
  };
  const onClickContactHandler = () => {
    // setActiveSection("contactRef");
    contactRef.current.scrollIntoView({ behavior: "smooth" });
  };
  return (
    <>
      <NavigationBar
        onClickPortFolio={onClickPortFolioHandler}
        onclickAbout={onClickAboutHandler}
        onClickContact={onClickContactHandler}
      />
      <Main />
    </>
  );
};

export default App;
